// src/magic-string.ts
import MagicStringBase from "magic-string";
var MagicString = class extends MagicStringBase {
  removeNode(node) {
    this.remove(node.start, node.end);
    return this;
  }
  sliceNode(node, offset = 0) {
    return this.slice(offset + node.start, offset + node.end);
  }
};

export {
  MagicString
};
